<?phpinclude 'defines.php';$post = (!empty($_POST)) ? true :false;if($post) {    $name = stripcslashes($_POST['name']);    $phone = stripcslashes($_POST['phone']);    $email = stripcslashes($_POST['email']);    $type = stripcslashes($_POST['type']);    switch ($type) {        case 'gold' :            $typeForm = "iRing Gold";            break;        case 'rose_gold' :            $typeForm = "iRing Rose Gold";            break;        case 'black' :            $typeForm = "iRing Black";            break;        case 'white' :            $typeForm = "iRing White";            break;        case 'grey' :            $typeForm = "iRing Graphite Grey";            break;        case 'silver' :            $typeForm = "iRing Silver";            break;        case 'mint' :            $typeForm = "iRing Mint Green";            break;        case 'rose' :            $typeForm = "iRing Rose";            break;        default:            $typeForm = "iRing без цвета";    }    $hook = stripcslashes($_POST['ihook']);    if ($hook == 'hook'){        $hookType = "С Hook";    } else {        $hookType = "Без Hook";    }    $subject = 'Заявка';    $error = '';    $message = '            <html>                <head>                    <title>Заявка</title>                </head>                <body>                    <p>Имя: '.$name.'</p>                    <p>Телефон: '.$phone.'</p>                    <p>Email: '.$email.'</p>                    <p>Продукт: '.$typeForm.'</p>                    <p>Модификация: '.$hookType.'</p>                </body>            </html>';    if(!$error) {        $mail = mail(CONTACT_FORM, $subject, $message,            "From: YourBestBuy@shop\r\n"            ."Content-type: text/html; charset=utf-8 \r\n"            ."X-Mailer: PHP/" . phpversion()        );        if($mail) {            echo "OK";        }    }    else {        echo '<div class="bg-danger">'.$error.'</div>';    }}